import React from 'react'

function Table(props) {
    return (
        <table>
            <tr>
                <th>Name</th>
                <th>Description</th>
                <th>Price</th>
            </tr>
            {props.entries.map((elem) => {
                return (
                <tr key={elem.id}>
                    <td>{elem.name}</td>
                    <td>{props.entries.desc}</td>
                    <td>{props.entries.price}</td>
                    <button onClick={props.handleDelete}>X</button>
                </tr>
            )})}

        </table>
    )
}

export default Table;
